
public class Ex4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float f; // 4byte
		double d; // 8byte
		
		System.out.println(10.5);
		
		f = 10.5f;
		d = 10.5;
		System.out.println(f);
		System.out.println(d);
		
		f = 0.1234567890123456789f;
		d = 0.1234567890123456789;
		System.out.println(f);
		System.out.println(d);
		
		double i = 3e6;
		System.out.println(i);
		
		i = 3e2;
		System.out.println(i);
		
		
	}

}
