
public class DenyLogicOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean play = true;
		System.out.println(play);	// true
		
		play = !play;
		System.out.println(play);	// false
		
		play = !play;
		System.out.println(play);	// true
		
	}

}
