
public class Ex2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i1;
		i1 = 10;
		System.out.println(i1);
		
		// 나머지 연산자
		int x = 10;
		int y = 3;
		int result = x % y;
		System.out.println(result);
		
		// 배수 확인 문제
		int z = 12;
		result = z % 3;	// result가 0이면 3의 배수
		System.out.println(result);
		
		result = z % 4;	// 4의 배수
		System.out.println(result);

		// 문자열 연결 연산자
		String str = "hello" + "java";
		System.out.println(str);
		
	}

}
