
public class Ex9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/* 나무를 n번 찍었습니다.
		 * 나무가 넘어갑니다.
		 */
		
		for (int i = 1; i <= 10; i++) {
			System.out.println("나무를 " + i + "번 찍었습니다.");
			if (i == 10) {
				System.out.println("나무가 넘어갑니다.");
			}
		}

	}

}
