
public class Ex10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int dan = 2;
		while(dan <= 4) {
			int gop = 1;
			while(gop <= 9) {
				System.out.println(dan + " * " + gop + " = " + dan * gop);
				gop++;
			}
			dan++;
		}
		
		System.out.println("");
		
	}

}
