
public class Ex7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 4단부터 6단까지 출력
		for(int i = 4; i <= 6; i++)	{
			for(int j = 1; j <= 9; j++) {
				System.out.println(i + " * " + j + " = " + i * j);
			}
		}
		
		System.out.println("");
		
	}

}
