
public class Ex4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0;
		++i;
		System.out.println(i);
		
		++i;
		System.out.println(i);
		
		++i;
		System.out.println(i);
		
		int j = 1;
		System.out.println(++j);	// 2 (j = j + 1)
		
		int k = 1;
		System.out.println(k++);	// 1
		System.out.println(k);		// 2
		
	}

}
